import React, { Component } from 'react'
import { Card, CardContent, CardActions, Dialog, DialogActions, DialogContent, InputBase, Divider, DialogTitle } from '@material-ui/core';
import "../App.css"
import archiveIcon from '../svg_icons/archive_menu.svg'
import reminderIcon from '../svg_icons/reminder.svg'
import collaboratorIcon from '../svg_icons/collaborator.svg'
import addimageIcon from '../svg_icons/addimage.svg'
import moreIcon from '../svg_icons/more.svg'
import ColorComponent from './ColorComponent';
import NoteService from '../services/NoteService'

const UpdateFunc = new NoteService().updateANote


export default class NoteItem extends Component {
    constructor(props){
        super(props);
        this.state={
            DialogOpen:false,
            id:this.props.noteobj.id,
            title:this.props.noteobj.title,
            text: this.props.noteobj,
            picture: null,
            is_archive: false,
            is_Trash: false,
            is_pinned: false,
            reminder: null,
            url: null,
            color:'#fff',
            label: [],
            collaborator: [],

        }
    }

    changeColor=(data)=>{
        this.setState({
            color:data,

        })
        console.log(this.state)
    }


    handleDialogOpen=()=>{
        this.setState({
            DialogOpen:!this.state.DialogOpen
        })
    }

    handleOnChange=(event)=>{
        this.setState({
            [event.target.name]:event.target.value,
        })
        console.log("onchangeDialog", this.state)

    }

    UpdateSerFunc=()=>{
        let UpdateData ={
            "title":  this.state.title,
            "text": this.state.text,
            "picture": this.state.picture,
            "is_archive": this.state.is_archive,
            "is_Trash": this.state.is_Trash,
            "is_pinned": this.state.is_pinned,
            "reminder": this.state.reminder,
            "url": this.state.url,
            "color": this.state.color,
            "user": sessionStorage.getItem('userid'),
            "label":this.state.label,
            "collaborator": this.state.collaborator
        }

        UpdateFunc(UpdateData)
        .then(res=>{
            this.props.noteGetFunc()
            this.setState({
                title:"",
                text: "",
                picture: null,
                is_archive: false,
                is_Trash: false,
                is_pinned: false,
                reminder: null,
                url: null,
                color: null,
                label: [],
                collaborator: []

            })
            console.log(" after update", res.data);

        })
        .catch(error=>{
            console.log("error data", error.response.data)
        })

    }

    render() {

        var noteCardShadow = "3px 5px 10px grey"
        return (
            <div >
                <Card className="NoteCard" style={{ background: this.state.color,  boxShadow: noteCardShadow }}>
                    <CardContent onClick={this.handleDialogOpen}>
                        <p>{this.props.noteobj.title}</p>
                        <p>{this.props.noteobj.text}</p>
                    </CardContent>
                    <CardActions>
                        <div className="flex-container">
                            <div><img src={reminderIcon} alt="archiveIcon" /></div>
                            <div><img src={collaboratorIcon} alt="archiveIcon" /></div>
                            <div><ColorComponent  id={this.state.id} changeColor={this.changeColor} /></div>
                            <div><img src={addimageIcon} alt="archiveIcon" /></div>
                            <div><img src={archiveIcon} alt="archiveIcon" /></div>
                            <div><img src={moreIcon} alt="archiveIcon" /></div>
                        </div>
                    </CardActions>

                </Card>
{/* ------------------------------------------------------------------------------------- */}
                <Dialog
                className="dialogbox"
                open={this.state.DialogOpen}
                PaperProps={{
                    style:{
                        width: "90%",
                        height: "auto"
                    }
                }}
                >
                    <DialogTitle>
                        <InputBase
                        name="title"
                        onChange={this.handleOnChange}
                        style={{width:500}}
                        placeholder="Title"
                        defaultValue={this.props.noteobj.title}	
                        />
                        </DialogTitle>
                        <Divider/>
                    <DialogContent>
                        <InputBase
                        name="text"
                        onChange={this.handleOnChange}
                        multiline={true}
                        style={{width:500}}
                        placeholder="Text"
                        defaultValue={this.props.noteobj.text}
                        />
                    </DialogContent>                       
                    <DialogActions>
                    <div className="flex-containerDialog">
                            <div><img src={reminderIcon} alt="archiveIcon" /></div>
                            <div><img src={collaboratorIcon} alt="archiveIcon" /></div>
                            <div><ColorComponent id={this.state.id} changeColor={this.changeColor} /></div>
                            <div><img src={addimageIcon} alt="archiveIcon" /></div>
                            <div><img src={archiveIcon} alt="archiveIcon" /></div>
                            <div><img src={moreIcon} alt="archiveIcon" /></div>

                        </div>
                        <div onClick={this.UpdateSerFunc}>Close</div>
                    </DialogActions>
                </Dialog>


            </div>
        )
    }
}
