import React, { Component } from 'react'
import { Menu, MenuItem } from '@material-ui/core';
import reminderIcon from '../svg_icons/reminder.svg'

export default class ReminderComponent extends Component {
    constructor(){
        super();
        this.state={
            menuOpen:false,
            anchorEl:null,

        }
    }

    handleMenu=(e)=>{
        this.setState({
            menuOpen:true,
            anchorEl:e.target
        })
    }

    handleClose = e =>{
        this.setState({
            menuOpen: !this.state.menuOpen,
            anchorEl: this.state.anchorEl
        })
    }

    reminderDate=(e)=>{
        var today = new Date()
        var date = today.getDate()
        today.setHours(8,0,0)

    }


    render() {
        return (
            <div>
                <img src={reminderIcon} alt="archiveIcon"
                aria-controls="menu-appbar"
                // aria-haspopup="true"
                onClick={this.handleMenu} 
                />

                <Menu
                 id="menu-appbar"
                 anchorEl={this.state.anchorEl}
                 open={this.state.menuOpen}
                 onClose={this.handleClose}>
                    <MenuItem>Reminder:</MenuItem>
                    <MenuItem><p>Later today    :    8:00 PM</p></MenuItem>
                    <MenuItem>Tomorrow 8:00 PM</MenuItem>
                    <MenuItem>Next week 8:00 PM</MenuItem>
                </Menu>
            </div>
        )
    }
}
