import React, { Component } from 'react'
import labeledt from '../svg_icons/label_edit.svg'
import deleteIcon from '../svg_icons/delete.svg'
import labelIcon from '../svg_icons/label.svg'
import { InputBase, ListItemText, ListItemIcon, ListItem } from '@material-ui/core';
import NoteService from '../services/NoteService';



const LabelDelete = new NoteService().deleteLabel

export default class DialogLabelEdit extends Component {
    constructor(props){
        super(props);
        this.state={
            label:props.label.name,
            labelId:props.label.id,
            isHover:false,
            renameLabel:false,


        }
    }




    handleMouse=()=>{
        this.setState({
            isHover:true
        })
    }

    handleMouseLeave=()=>{
        this.setState({
            isHover:false
        })
    }


    deleteLabel=()=>{

        LabelDelete(this.state.labelId)
        .then(res=>{
            this.props.LabelsGet()
            console.log("delete response", res.data)
        })
        .catch(error=>{
            console.log("error in delete", error.response.data)
        })
    }


    handleOnChange=(event)=>{
        this.setState({
            [event.target.name]:event.target.value
        })
    }


    handleLblEdit=()=>{
        this.setState({
            renameLabel:true
        })
    }

    render() {
        return (
            <div>           
                 <ListItem onMouseEnter={this.handleMouse} onMouseLeave={this.handleMouseLeave} className="DialogNote" id="DialogNote" key={this.props.label.id}>
                {this.state.isHover 
                    ? <ListItemIcon src={deleteIcon} onClick={this.deleteLabel} alt="deletesvg" />
                    : <ListItemIcon src={labelIcon} alt="labelsvg" />
                }

                <div className="DgEditLabel" >

                    {this.state.renameLabel ? <InputBase
                                                name="label"
                                                defaultValue={this.state.label}
                                                onChange={this.handleOnChange}

                                                />
                        :<ListItemText>{this.props.label.name}</ListItemText>

                    }
                      <ListItemIcon onClick={this.handleLblEdit} src={labeledt} alt="labelsvg" />
                </div>
            </ListItem>
            </div>

        )
    }
}
